  function insertSpanRow(i, colspan, content) {
    var table = document.getElementById('tinytable_v4vq4lvkv73rwa1e7w62');
    var newRow = table.insertRow(i);
    var newCell = newRow.insertCell(0);
    newCell.setAttribute("colspan", colspan);
